import React, { useState } from "react";
import "./App.css";
import handleSubmit from "./buttonHandler.js";

function App() {
  const [url, setUrl] = useState("");
  const [shortCode, setShortCode] = useState("");
  const [shortenedUrl, setShortenedUrl] = useState("");
  const [error, setError] = useState("");

  const onSubmit = async () => {
    setError("");          // reset error
    setShortenedUrl("");   // reset previous result

    try {
      // Pass url and shortCode to handleSubmit and get the short URL
      const shortUrl = await handleSubmit(url.trim(), shortCode.trim());
      setShortenedUrl(shortUrl);
    } catch (err) {
      setError(err.message || "Error shortening URL");
    }
  };

  return (
    <div className="app-container">
      <main>
        <h1>Ever dreamed of shortening a URL?</h1>
        <div className="box">
          <h2 className="sub-headers">Please enter a URL:</h2>
          <input
            type="text"
            placeholder="https://example.com"
            className="input"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
          />
          <h2 className="sub-headers">Please enter a custom short code:</h2>
          <input
            type="text"
            placeholder="custom short code"
            className="input-short-code"
            value={shortCode}
            onChange={(e) => setShortCode(e.target.value)}
          />
          <button type="submit" className="submit-btn" onClick={onSubmit}>
            Convert URL
          </button>

          {/* Display shortened URL */}
          {shortenedUrl && (
            <>
              <h2 className="sub-headers" style={{ marginTop: "1rem" }}>
                Shortened URL:
              </h2>
              <input
                type="text"
                className="input"
                value={shortenedUrl}
                readOnly
                onFocus={(e) => e.target.select()}
              />
            </>
          )}

          {/* Display error if any */}
          {error && <p style={{ color: "red", marginTop: "1rem" }}>{error}</p>}
        </div>
      </main>

      <hr className="footer-divider" />
      <footer>Â© All rights reserved to Guy Harpaz</footer>
    </div>
  );
}

export default App;
