import React from "react";
import "./App.css";
import handleSubmit from "./buttonHandler.js";

function App() {
  return (
    <div className="app-container">
      <h1>
        Ever dreamed about shortening a URL?
      </h1>
      <div className="box">
        <h2 className="enter-url">Please enter a URL:</h2>
        <input
          type="text"
          placeholder="https://example.com"
          className="url-input"
        />
        <button
          type="submit"
          className="submit-btn"
          onClick={handleSubmit}
        >
          Convert URL
        </button>
      </div>
      <footer>Â© All rights reserved to Guy Harpaz</footer>
    </div>
  );
}

export default App;
